import React, { Component } from "react";
import App from "./App";
import RollResultsSection from "./RollResultsSection";

class DiceRollButton extends Component {
  constructor(props) {
    super(props);
    this.callCheckAPI = this.callCheckAPI.bind(this);
    this.state = {
      checkResponse: {
        rolls: [],
        rollstring: "",
        rolltotal: "",
        basestring: "",
        name: "",
        rolltype: "",
      }
    }
  }

  callCheckAPI() {
    fetch(`http://localhost:9000/rollcheck?name=${this.props.name}&rolltype=${this.props.rolltype}&die=${this.props.die}&num=${this.props.num}&mod=${this.props.mod}`)
        .then(res => res.json())
        .then(res => this.setState({ checkResponse: res }));

    rollcontainer = document.getElementById('rollcontainer')
    rollcontainerlist = rollcontainer.querySelectorAll('.SingleRollResult');
    if (rollcontainerlist.length < 4) {
      
      let newid = rollresultelementslist.length;
      newrollresult.setAttribute('id', `rollResult${newid}`);
      newrollresult.setAttribute('display', 'grid');
      
      rollresultcontainer.appendChild(newrollresult);
  
  
    } else {
      // we need to cycle the roll results
      let rollresult1 = rollresultcontainer.querySelector('#rollResult1');
      let rollresult2 = rollresultcontainer.querySelector('#rollResult2');
      let rollresult3 = rollresultcontainer.querySelector('#rollResult3');
  
      let roll1info = rollresult1.innerHTML;
      let roll2info = rollresult2.innerHTML;
  
      rollresult2.innerHTML = roll1info;
      rollresult3.innerHTML = roll2info;
  
      newrollresult = document.getElementById('rollResult1');
    }
    //RollResultsSection.displayRollResults(this.state.checkResponse.name, this.state.checkResponse.rolltype, this.state.checkResponse.rollstring, this.state.checkResponse.rolltotal, this.state.checkResponse.basestring);
  }
  
  render (){
    console.log(`rollstring = ${this.state.checkResponse.rollstring}`)
    return (
    <button onClick={this.callCheckAPI}>{this.props.mod}</button>
    );
  }
};

export const rollResults = () => {
  return ; 
};

export default DiceRollButton;
