import React, { Component } from "react";
import SkillRow from './SkillRow'

class SkillSection extends Component {
  constructor(props) {
    super(props);
    this.callCheckAPI = this.getSkills.bind(this);
    this.state = {
      checkResponse: {
        skills: []
        }
      }
    }

  getSkills() {
    fetch(`http://localhost:9000/getcharacterinfo?infotype=skill`)
        .then(res => res.json())
        .then(res => this.setState({ checkResponse: res }));
  }
  
  render () {
    this.getSkills();
    console.log(`rollstring = ${this.state.checkResponse.rollstring}`)
    return (
        <div className="skillsBox frontElement">
          <table class="skillsTable">
            <tr>
              <th>Prof</th>
              <th>Mod</th>
              <th>Skill</th>
              <th>Bonus</th>
            </tr>
            {this.state.checkResponse.skills.map((skill) => <SkillRow name={skill.skillname} mod={skill.skillmod} prof={skill.skillprof} bonus={skill.skillbonus} />)}
          </table>
        </div>
    );
  }
};

export default SkillSection;
