import { useEffect, useState } from 'react';
import SingleRollResult from './SingleRollResult';
import io from 'socket.io-client';


export default function RollResultsSection () {
  const [rolllist, setRollList] = useState({rolllistinner: [
    {
      rolls: [ 3 ],
      rollstring: '3+2',
      rolltotal: 5,
      basestring: '1d20+2',
      name: 'Constitution',
      rolltype: 'Ability'
    }
  ]});
  const socket = io.connect('http://localhost:4000');
  

  useEffect(() => {
    socket.on('rolldiceresult', (data) => {
      setRollList(...rolllist.rolllistinner, data);
      console.log(rolllist.rolllistinner);
  })});


  return (
      <div id="rollContainer">
        {rolllist.rolllistinner.map((roll) => <SingleRollResult name={roll.name} rolltype={roll.rolltype} rollstring={roll.rollstring} basestring={roll.basestring} rolltotal={roll.rolltotal}/>)}
      </div> 
  )
}
