import React, { useEffect, useState } from 'react';
import { Stack, Button } from 'react-bootstrap';

function HealthSection () {
  const [healthval, setHealthVal] = useState({
    health: 11,
  })

  useEffect(() => {
    getHealth();
    } 
  )
/*
  const setHealth = () => {

  }
*/
  const getHealth = () => {
    fetch(`http://localhost:9000/getcharacterinfo?infotype=health`)
    .then(res => res.json())
    .then(res => setHealthVal(res));
  }

  return ( 
    <div class="healthSection">
      <div class='leftHealthSection'>
        <Stack direction="vertical" gap={2}>
          <span>Hit Points</span>
          <Button variant='secondary' size='sm'>Heal</Button>
          <input type='number'></input>
          <Button variant='secondary' size='sm'>Damage</Button>
        </Stack>
      </div>
      <div class='rightHealthSection'>
        <Stack direction="horizontal">
          <input type="number" defaultValue={healthval.health}></input>
          <input type="number" value={healthval.health}></input>
        </Stack>
      </div>
    </div>
      
  );
 
}

export default HealthSection;
