import React, { Component } from "react";
import logo from './logo.svg';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      bum: "Hell yeah brothers",
      checkVars: {
        name: "Strength",
        rolltype: "Ability",
        die: 20,
        num: 1,
        mod: 2,
      },
      checkResponse: {
        rolls: [],
        rollstring: "",
        rolltotal: 0,
        basestring: "",
        name: "",
        rolltype: "",
      }
      /*
      apiResponse: {
        item: {
          name: "",
          value: 0,
          description: "",
          weight: 0,
          rarity: {
            rarityname: "",
            raritydescription: "",
          }
        },
        armorclass: 0,
        strengthreq: 0,
        stealthdisadvantage: false,
        word: 'neat mode',
      }
      */

    }
  }

  callCheckAPI() {
    fetch(`http://localhost:9000/rollcheck?name=${this.state.name}&rolltype=${this.state.rolltype}&die=${this.state.die}&num=${this.state.num}&mod=${this.state.mod}`)
        .then(res => res.text())
        .then(res => this.setState({ checkResponse: res }));
  }

  componentDidMount() {
    this.callCheckAPI();
  }

  render () {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>
            Edit <code>src/App.js</code> and save to reload.
          </p>
          <a
            className="App-link"
            href="https://reactjs.org"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </header>
        <p className="App-intro">{this.state.checkResponse.rolltotal}</p>
      </div>
    );
  }
}

export default App;
