import React, { Component, createElement } from "react";
import App from "./App";
import RollResultsSection from "./RollResultsSection";
import SingleRollResult from "./SingleRollResult";
import Button from 'react-bootstrap/Button';

import io from 'socket.io-client';
const socket = io.connect('http://localhost:4000');


class DiceRollButton extends Component {
  constructor(props) {
    super(props);
    this.callCheckAPI = this.callCheckAPI.bind(this);
    this.rollDice = this.rollDice.bind(this);
    this.state = {
      /*
      checkResponse: {
        rolls: [],
        rollstring: "",
        rolltotal: "",
        basestring: "",
        name: "",
        rolltype: "",
      */
      data: {
        name: this.props.name, 
        rolltype: this.props.rolltype,
        die: this.props.die,
        num: this.props.num,
        mod: this.props.mod
      }
    }
  }

  rollDice() {
    socket.emit('rolldice', this.state.data);
  }

  callCheckAPI() {
    fetch(`http://localhost:9000/rollcheck?name=${this.props.name}&rolltype=${this.props.rolltype}&die=${this.props.die}&num=${this.props.num}&mod=${this.props.mod}`)
        .then(res => res.json())
        .then(res => this.setState({ checkResponse: res }));
/*
    let rollcontainer = document.getElementById('rollContainer')
    let rollcontainerlist = rollcontainer.querySelectorAll('.SingleRollResult');
    if (rollcontainerlist.length < 4) {
      let newrollresult = createElement(SingleRollResult, {rollstring={this.state.checkResponse.rollstring} rolltotal={this.state.checkResponse.rolltotal} name={this.state.checkResponse.name}})
      rollcontainer.appendChild(newrollresult);
      }
  */
    
    //RollResultsSection.displayRollResults(this.state.checkResponse.name, this.state.checkResponse.rolltype, this.state.checkResponse.rollstring, this.state.checkResponse.rolltotal, this.state.checkResponse.basestring);
  }
  
  render (){
    console.log(`rollstring = ${this.state.checkResponse.rollstring}`)
    return (
    <Button variant='secondary' size='xl' onClick={this.rollDice}>{this.props.mod}</Button>
    );
  }
};

export default DiceRollButton;
