import React, { useState } from 'react';

export default function DiceRoller ({ sides }) {
  const rollDie = () => Math.floor(Math.random() * sides) + 1;
  const [results, setResults] = useState([]);

  const rollDice = () => {
    const rolls = Array.from({ length: 3 }, () => rollDie());
    setResults(rolls);
  };

  return (
    <div>
      <button onClick={rollDice}>Roll {sides}-sided Dice</button>
      {results.length > 0 && <div>Results: {results.join(', ')}</div>}
    </div>
  );
};
