import { useEffect, useState } from 'react';
import SingleRollResult from './SingleRollResult';
import io from 'socket.io-client';


export default function RollResultsSection () {
  const [rolls, setRolls] = useState([
    {
      rolls: [ 11 ],
      rollstring: '11+2',
      rolltotal: 13,
      basestring: '1d20+2',
      name: 'Dexterity',
      rolltype: 'Ability'
    }
  ]);
  const socket = io.connect('http://localhost:4000');
  

  useEffect(() => {
    socket.on('rolldiceresult', (data) => {
      let temprolls = rolls;
      temprolls(data);
      setRolls(temprolls);
  })});


  return (
      <div id="rollContainer">
        {rolls.map((roll) => <SingleRollResult name={roll.name} rolltype={roll.rolltype} rollstring={roll.rollstring} basestring={roll.basestring} rolltotal={roll.rolltotal}/>)}
      </div> 
  )
}
